    #region 剪裁 -- 用GDI+
    /// <summary>
    /// 剪裁 -- 用GDI+
    /// </summary>
    /// <param name="b">原始Bitmap</param>
    /// <param name="StartX">开始坐标X</param>
    /// <param name="StartY">开始坐标Y</param>
    /// <param name="iWidth">宽度</param>
    /// <param name="iHeight">高度</param>
    /// <returns>剪裁后的Bitmap</returns>
    public Bitmap KiCut(Bitmap b)
    {
        if (b == null)
        {
            return null;
        }

        int w = b.Width;
        int h = b.Height;

        if (X >= w || Y >= h)
        {
            return null;
        }

        if (X + Width > w)
        {
            Width = w - X;
        }

        if (Y + Height > h)
        {
            Height = h - Y;
        }

        try
        {
            Bitmap bmpOut = new Bitmap(Width, Height, PixelFormat.Format24bppRgb);

            Graphics g = Graphics.FromImage(bmpOut);
            g.DrawImage(b, new Rectangle(0, 0, Width, Height), new Rectangle(X, Y, Width, Height), GraphicsUnit.Pixel);
            g.Dispose();

            return bmpOut;
        }
        catch
        {
            return null;
        }
    }

    public int X = 0;
    public int Y = 0;
    public int Width = 120;
    public int Height = 120;
    public ImageCut(int x, int y, int width, int heigth)
    {
        X = x;
        Y = y;
        Width = width;
        Height = heigth;
    }
    public ImageCut() { }
    #endregion

    #region 图片 base64编码相互转换(文本)
    //图片 转为    base64编码的文本
    public string ImgToBase64String(string Imagefilename)
    {
        string strRetrun = "";
        try
        {
            Bitmap bmp = new Bitmap(Imagefilename);
            var Image = bmp;
            FileStream fs = new FileStream(Imagefilename + ".txt", FileMode.Create);
            StreamWriter sw = new StreamWriter(fs);

            MemoryStream ms = new MemoryStream();
            bmp.Save(ms, System.Drawing.Imaging.ImageFormat.Jpeg);
            byte[] arr = new byte[ms.Length];
            ms.Position = 0;
            ms.Read(arr, 0, (int)ms.Length);
            ms.Close();
            String strbaser64 = Convert.ToBase64String(arr);
            sw.Write(strbaser64);

            sw.Close();
            fs.Close();
            return strRetrun = "1";
        }
        catch (Exception ex)
        {
            return strRetrun = "ImgToBase64String 转换失败/nException:" + ex.Message;
        }
    }

    //base64编码的文本 转为    图片
    public string Base64StringTxtToImage(string txtFileName)
    {
        string strRetrun = "";
        try
        {
            FileStream ifs = new FileStream(txtFileName, FileMode.Open, FileAccess.Read);
            StreamReader sr = new StreamReader(ifs);

            String inputStr = sr.ReadToEnd();
            byte[] arr = Convert.FromBase64String(inputStr);
            MemoryStream ms = new MemoryStream(arr);
            Bitmap bmp = new Bitmap(ms);

            bmp.Save(txtFileName + ".jpg", System.Drawing.Imaging.ImageFormat.Jpeg);
            //bmp.Save(txtFileName + ".bmp", ImageFormat.Bmp);
            //bmp.Save(txtFileName + ".gif", ImageFormat.Gif);
            //bmp.Save(txtFileName + ".png", ImageFormat.Png);
            ms.Close();
            sr.Close();
            ifs.Close();
            var Image = bmp;
            return strRetrun = "1";
        }
        catch (Exception ex)
        {
            return strRetrun = "Base64StringToImage 转换失败/nException：" + ex.Message;
        }
    }

